mapper.select:
select p.product_no productNo, p.main_img mainImg, p.category, p.product_name productName, r.star star, p.price from product p left join (select product_no, avg(star) star from reviews group by product_no) r on p.product_no = r.product_no
where p.product_name like concat('%',#{keyword},'%')
order by p.product_no desc limit #{startRowNo}, #{listCount}
